import{_ as a,c as r,o,R as e}from"./chunks/framework.DkRcaExU.js";const c=JSON.parse('{"title":"商汤秒画（SenseMirage）介绍","description":"","frontmatter":{"title":"商汤秒画（SenseMirage）介绍"},"headers":[],"relativePath":"docs/miaohua.md","filePath":"docs/miaohua.md","lastUpdated":1742823817000}'),n={name:"docs/miaohua.md"};function l(s,t,i,h,d,g){return o(),r("div",null,t[0]||(t[0]=[e('<h1 id="商汤秒画-sensemirage" tabindex="-1">商汤秒画（SenseMirage） <a class="header-anchor" href="#商汤秒画-sensemirage" aria-label="Permalink to &quot;商汤秒画（SenseMirage）&quot;">​</a></h1><h2 id="_1-概述" tabindex="-1">1. 概述 <a class="header-anchor" href="#_1-概述" aria-label="Permalink to &quot;1. 概述&quot;">​</a></h2><p><a href="https://miaohua.sensetime.com/" target="_blank" rel="noreferrer"><strong>秒画（SenseMirage）</strong></a> 是商汤科技推出的一款 <strong>AI 绘画平台</strong>，用户可以通过输入文本提示词或上传图片，快速生成高质量的 AI 画作。秒画支持多种绘画风格，并提供 <strong>LoRA 训练</strong> 及 <strong>风格迁移</strong> 功能，适用于多个创意领域。</p><hr><h2 id="_2-主要功能" tabindex="-1">2. 主要功能 <a class="header-anchor" href="#_2-主要功能" aria-label="Permalink to &quot;2. 主要功能&quot;">​</a></h2><h3 id="🎨-ai-文生图-文本生成图像" tabindex="-1">🎨 AI 文生图（文本生成图像） <a class="header-anchor" href="#🎨-ai-文生图-文本生成图像" aria-label="Permalink to &quot;🎨 AI 文生图（文本生成图像）&quot;">​</a></h3><p>用户只需输入文本描述，AI 便可生成对应的画作，支持多种风格，如：</p><ul><li><strong>二次元</strong></li><li><strong>写实风</strong></li><li><strong>赛博朋克</strong></li><li><strong>水墨风</strong></li><li><strong>抽象艺术</strong></li><li><strong>科幻设定</strong> 等。</li></ul><h3 id="🖼️-图生图-图片生成图片" tabindex="-1">🖼️ 图生图（图片生成图片） <a class="header-anchor" href="#🖼️-图生图-图片生成图片" aria-label="Permalink to &quot;🖼️ 图生图（图片生成图片）&quot;">​</a></h3><ul><li>通过上传图片，AI 进行风格迁移、细节优化或画面扩展。</li><li>适用于 <strong>设计、绘画参考</strong> 或 <strong>海报生成</strong>。</li></ul><h3 id="🚀-lora-训练-个性化训练" tabindex="-1">🚀 LoRA 训练（个性化训练） <a class="header-anchor" href="#🚀-lora-训练-个性化训练" aria-label="Permalink to &quot;🚀 LoRA 训练（个性化训练）&quot;">​</a></h3><ul><li>用户可上传自己的图片，结合秒画 <strong>LoRA 训练功能</strong>，训练出定制化的风格模型。</li><li>适用于 <strong>角色设定、IP 设计、品牌视觉</strong> 等应用场景。</li></ul><h3 id="💡-灵感广场" tabindex="-1">💡 灵感广场 <a class="header-anchor" href="#💡-灵感广场" aria-label="Permalink to &quot;💡 灵感广场&quot;">​</a></h3><ul><li><strong>查看他人作品</strong>，获取创作灵感。</li><li><strong>一键复刻</strong>，使用相同的提示词快速生成相似风格的作品。</li></ul><h3 id="⚙️-生成参数控制" tabindex="-1">⚙️ 生成参数控制 <a class="header-anchor" href="#⚙️-生成参数控制" aria-label="Permalink to &quot;⚙️ 生成参数控制&quot;">​</a></h3><ul><li><strong>步数调整</strong>：影响画作精细程度。</li><li><strong>风格权重</strong>：控制 AI 对提示词的遵循程度。</li><li><strong>多种模型选择</strong>，适配不同风格需求。</li></ul><hr><h2 id="_3-使用方法" tabindex="-1">3. 使用方法 <a class="header-anchor" href="#_3-使用方法" aria-label="Permalink to &quot;3. 使用方法&quot;">​</a></h2><h3 id="✅-访问秒画官网" tabindex="-1">✅ 访问秒画官网 <a class="header-anchor" href="#✅-访问秒画官网" aria-label="Permalink to &quot;✅ 访问秒画官网&quot;">​</a></h3><p>👉 <a href="https://miaohua.sensetime.com/" target="_blank" rel="noreferrer"><strong>秒画官网</strong></a></p><h3 id="🔹-账号注册与登录" tabindex="-1">🔹 账号注册与登录 <a class="header-anchor" href="#🔹-账号注册与登录" aria-label="Permalink to &quot;🔹 账号注册与登录&quot;">​</a></h3><ul><li>支持 <strong>手机号</strong> 或 <strong>第三方账号</strong> 登录。</li></ul><h3 id="🔹-生成图像" tabindex="-1">🔹 生成图像 <a class="header-anchor" href="#🔹-生成图像" aria-label="Permalink to &quot;🔹 生成图像&quot;">​</a></h3><ol><li>在输入框中输入 <strong>提示词（Prompt）</strong>。</li><li>选择 <strong>模型</strong> 及 <strong>风格参数</strong>。</li><li>点击 <strong>生成</strong>，等待 AI 处理。</li><li>预览并下载图片，或进一步调整参数优化效果。</li></ol><h3 id="🔹-训练-lora-模型-可选" tabindex="-1">🔹 训练 LoRA 模型（可选） <a class="header-anchor" href="#🔹-训练-lora-模型-可选" aria-label="Permalink to &quot;🔹 训练 LoRA 模型（可选）&quot;">​</a></h3><ol><li>上传多张参考图。</li><li>设置训练参数，等待 AI 训练完成。</li><li>使用训练好的模型生成专属风格的作品。</li></ol><hr><h2 id="_4-适用场景" tabindex="-1">4. 适用场景 <a class="header-anchor" href="#_4-适用场景" aria-label="Permalink to &quot;4. 适用场景&quot;">​</a></h2><div class="vp-table__container"><table><thead><tr><th>场景</th><th>应用方向</th></tr></thead><tbody><tr><td><strong>设计</strong></td><td>插画、概念设计、品牌视觉</td></tr><tr><td><strong>影视</strong></td><td>角色设定、场景概念、美术风格</td></tr><tr><td><strong>游戏</strong></td><td>角色原画、地图设定、UI 设计</td></tr><tr><td><strong>电商</strong></td><td>产品海报、营销素材、品牌包装</td></tr><tr><td><strong>艺术创作</strong></td><td>AI 艺术作品、个人创作、NFT</td></tr></tbody></table></div><hr><h2 id="_5-总结" tabindex="-1">5. 总结 <a class="header-anchor" href="#_5-总结" aria-label="Permalink to &quot;5. 总结&quot;">​</a></h2><p>商汤 <strong>秒画（SenseMirage）</strong> 是一款强大的 AI 绘画工具，适用于设计、影视、游戏、营销等多个领域。通过 <strong>文本生图、图生图、LoRA 训练</strong> 等功能，用户可以快速生成高质量的 AI 作品，并根据需求定制专属风格。</p><p>🔗 <strong>立即体验</strong>：<a href="https://miaohua.sensetime.com/" target="_blank" rel="noreferrer"><strong>秒画官网</strong></a></p>',33)]))}const m=a(n,[["render",l]]);export{c as __pageData,m as default};
